import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container } from 'react-bootstrap';
import { ListGroup } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import styled from 'styled-components';
import TaskForm from './TaskForm';

const ListContainer = styled.div`
    text-align: center;
`;

const TaskOverlay = styled.div`
    background-color: #bbff00;
    opacity: 0;
    transition: opacity 0.3s ease;
    overflow: hidden;
    width: 100%;
    height: 100%;
`;

const TaskBox = styled.div`
    &:hover ${TaskOverlay} {
        opacity: 0.9;
    };
    cursor: pointer;
`;

function TodolistPage() {
    const [lists, setLists] = useState([]);
    const [tasks, setTasks] = useState([]);
    const [isShown, setIsShown] = useState(false);
    const [value, setValue] = useState(false);

    const fetchLists = async () => {
        const { data: lists } = await axios.get('/api/todolist/');
        setLists(lists);
    }
    const fetchTasks = async () => {
        const { data: tasks } = await axios.get('/api/todolist/');
        setTasks(tasks);
    };

    const deleteTasks = async () => {
        const tasks = Document.getElementsByClassName('task');
        console.log(tasks);
    }

    const handleDelete = async (taskId) => {
        await axios.delete(`/api/todolist/${taskId}`);
        fetchTasks();
    }

    useEffect(() => {
        // fetchTasks();
        fetchLists();
    }, [])

    return (
        <Container fluid>
            <div className="d-flex mt-3 justify-content-center">
                <ListContainer>
                    <h1 className="m-3 pt-3">Welcome to TodoList</h1>
                    <div className="mb-2">
                        <TaskForm updateTasks={fetchTasks} />
                        {/* <form>
                            <label for="time">Time: </label>
                            <input type="text" id="time" name="time" placeholder="time"></input>
                            <label for="task">New task: </label>
                            <input type="text" id="task" name="task" placeholder="your new task"></input>
                            <Button ml-2 variant="warning" onClick={handleAdd}>Add a new task</Button>
                        </form> */}

                    </div>

                    <div className="pt-2 align-text-center">
                        {/* <form>
                            {tasks.map((task, index) =>
                                <div>
                                    <input type="checkbox" id="task" name="task" value="task" className="task" key={index}></input>
                                    <label for="task" className="m-1 task" key={index}>{task.time} —— {task.task}</label>
                                </div>
                            )}
                            <Button variant="warning" type="submit" className="m-2" onDelete={deleteTasks}>Completed!</Button>
                        </form> */}
                        {tasks.map((task, index) =>
                            <TaskBox key={index} className="m-1" onClick={() => handleDelete(task._id)}
                                onMouseEnter={() => setIsShown(true)}
                                onMouseLeave={() => setIsShown(false)}>
                                {isShown && (
                                    <TaskOverlay />
                                )}
                                {task.time} —— {task.task}
                            </TaskBox>
                        )}
                    </div>
                </ListContainer>
            </div>

        </Container>
    )
}

export default TodolistPage;
