import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container } from 'react-bootstrap';
import { Card } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { Link } from 'react-router-dom'
import styled from 'styled-components';
import ListForm from './ListForm';

function ListCard(list) {
    const [isShown, setIsShown] = useState(false);
    const [tasks, setTasks] = useState([]);

    const TaskOverlay = styled.div`
        background-color: #bbff00;
        opacity: 0;
        transition: opacity 0.3s ease;
        overflow: hidden;
        width: 100%;
        height: 100%;
    `;

    const TaskBox = styled.div`
        &:hover ${TaskOverlay} {
            opacity: 0.9;
        };
        cursor: pointer;
    `;

    const fetchTasks = async () => {
        const { data: lists } = await axios.get('/api/todolist/');
        setTasks(lists.tasks);
    };

    const handleDelete = async (listId) => {
        await axios.delete(`/api/todolist/${listId}`);
        list.updateLists();
    }

    useEffect(() => {
        console.log(list);
        // list.updateLists.fetchTasks();
    }, [list]);

    return (

        <Card style={{ width: '18rem' }}>
            <Card.Body>
                <Card.Title>{list.list.author}'s {list.list.title}</Card.Title>
                <Card.Text>
                    {list.list.date}
                    {list.list.tasks.map((task, index) =>
                        <TaskBox key={index} className="m-1">
                            {isShown && (
                                <TaskOverlay />
                            )}
                            {task}
                        </TaskBox>
                    )}
                </Card.Text>

                <Button variant="primary">Go somewhere</Button>
            </Card.Body>
        </Card>

        // <div className="pt-2 align-text-center">
        //     {list.list.author}'s {list.list.title} - 
        //     {list.list.tasks.map((task, index) =>
        //         <TaskBox key={index} className="m-1">
        //             {isShown && (
        //                 <TaskOverlay />
        //             )}
        //             {task}
        //         </TaskBox>
        //     )
        //     }
        //     <Button className="mb-3" variant="danger" onClick={() => handleDelete(list.list._id)}> Delete</ Button>
        // </div>

    )
}

export default ListCard;
