import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

function ListForm(props) {

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newTask = { task: e.target.task.value, time: e.target.time.value };
        console.log(newTask);
        await axios.post('/api/todolist/', newTask);
        props.updateTasks();
    }

    return (
        <Form onSubmit={handleSubmit}>
            <Form.Group controlId="task">
                <Form.Label>New task:</Form.Label>
                <Form.Control type="text" placeholder="Enter your new task" name="task" />
            </Form.Group>

            <Form.Group controlId="time">
                <Form.Label>Time:</Form.Label>
                <Form.Control type="text" placeholder="Time" name="time" />
            </Form.Group>
            <Button className="mt-3" variant="warning" type="submit">
                Submit
            </Button>
        </Form>
    )

}

export default ListForm;
