import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { useHistory } from 'react-router-dom';


import { Button } from 'react-bootstrap';

function ListShow(props) {
    const listId = props.match.params.listId;
    const [list, setList] = useState();
    const history = useHistory();
    const [isShown, setIsShown] = useState(false);

    const TaskOverlay = styled.div`
        background-color: #bbff00;
        opacity: 0;
        transition: opacity 0.3s ease;
        overflow: hidden;
        width: 100%;
        height: 100%;
    `;

    const TaskBox = styled.div`
        &:hover ${TaskOverlay} {
            opacity: 0.9;
        };
        cursor: pointer;
    `;

    const fetchList = async () => {
        const { data: list } = await axios.get(`/api/todolist/${listId}`);
        setList(list);
        console.log(list);
    }

    const handleDelete = async (listId) => {
        await axios.delete(`/api/todolist/${listId}`);
        history.replace('/api/todolist/');
    }

    useEffect(() => {
        fetchList();
        console.log(list);
    }, [])

    return (

        <div>
            {
                list && list.author && list.title && list.date && list.tasks &&
                <>
                    <div>
                        {list.author}'s {list.title}
                    </div>

                    {list.date}
                    {list.tasks.map((task, index) =>
                        <TaskBox key={index} className="m-1">
                            {/* {isShown && (
                        <TaskOverlay />
                    )} */}
                            {task}
                        </TaskBox>
                    )}
                </>
            }
            <Button className="mb-3" variant="danger" onClick={handleDelete}> Delete </ Button>
        </div>

        // <div></div>
    )
}

export default ListShow;
