import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

function ListEdit(props) {
    console.log(props)
    const listId = props.match.params.listId;
    const [list, setList] = useState();
    const [tasks, setTasks] = useState([]);

    const fetchList = async () => {
        const { data: list } = await axios.get(`/api/todolist/${listId}`);
        setList(list);
        console.log(list);
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log(e.target);
        const newTask = { title: e.target.title.value, author: e.target.author.value, date: e.target.date.value, tasks: [e.target.task.value], count: tasks.length };
        console.log(newTask);
        await axios.post('/api/todolist/', newTask);
        props.updateLists();
    }

    const handleAddTasks = async (e) => {
        e.preventDefault();
        console.log(e);
        const newTask = e.target.task.value;
        setTasks(...tasks, newTask);
        console.log(tasks);
    }

    const handleUpdate = async (listId) => {
        await axios.delete(`/api/todolist/${listId}`);
    }

    useEffect(() => {
        fetchList();
        console.log(list)
    }, [])

    return (
        <div>
            <div>
                <h2>Old version: </h2>
                {
                    list && list.author && list.title && list.date && list.tasks &&
                    <>
                        <div>
                            {list.author}'s {list.title}
                        </div>

                        {list.date}
                        {list.tasks.map((task, index) =>
                            <div key={index} className="m-1">
                                {task}
                            </div>
                        )}
                    </>
                }

            </div>

            <div mt-3 justify-content-center>
                <h2>Update</h2>
                <Form onSubmit={handleSubmit}>
                    <Form.Group controlId="title">
                        <Form.Label>List title:</Form.Label>
                        <Form.Control type="text" placeholder="Title" name="title" value={list.title} />
                    </Form.Group>

                    <Form.Group controlId="name">
                        <Form.Label>Name:</Form.Label>
                        <Form.Control type="text" placeholder="Name" name="author" />
                    </Form.Group>

                    <Form.Group controlId="date">
                        <Form.Label>Date:</Form.Label>
                        <Form.Control type="text" placeholder="Date" name="date" />
                    </Form.Group>

                    <Form.Group>
                        <Form.Label>Task:</Form.Label>
                        <Form.Control type="text" placeholder="Task" name="task" />
                        {/* <Button className="mt-3" variant="secondary" type="submit">+</Button> */}
                    </Form.Group>

                    <Button className="mt-3" variant="warning" type="submit">
                        Update
                    </Button>
                </Form>

            </div>




        </div>
    )

}

export default ListEdit;


