import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container } from 'react-bootstrap';
import { ListGroup } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import styled from 'styled-components';

const ListContainer = styled.div`
    align-items: center;
`;


function TodolistPage() {
    const [tasks, setTasks] = useState([]);
    useEffect(() => {
        let mounted = true;
        const fetchTasks = async () => {
            const { data: tasks } = await axios.get('/api/todolist/');
            if (mounted) setTasks(tasks);
        };
        fetchTasks();
    }, [tasks])

    return (
        <Container fluid>
            <div className="d-flex mt-2 mx-auto w-50">
                <ListContainer>
                    <h1 className="">Welcome to TodoList</h1>
                    <div className="mb-2">
                        <Button variant="warning">Add a new task</Button>
                    </div>

                    <div className="pt-2 align-text-center">
                        {tasks.map(task =>
                            <div className="p-1 align-text-center" >{task.task}</div>
                        )}
                    </div>
                </ListContainer>
            </div>

        </Container>
    )
}

export default TodolistPage;
