import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

function ListForm(props) {
    const [tasks, setTasks] = useState([]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log(e.target);
        const newTask = { title: e.target.title.value, author: e.target.author.value, date: e.target.date.value, tasks: tasks };
        console.log(newTask);
        await axios.post('/api/todolist/', newTask);
        props.updateTasks();
    }

    const handleAddTasks = async (e) => {
        const newTask = e.target.task.value;
        setTasks(...tasks, newTask);
    }

    return (
        <Form onSubmit={handleSubmit}>
            <Form.Group controlId="title">
                <Form.Label>List title:</Form.Label>
                <Form.Control type="text" placeholder="Title" name="title" />
            </Form.Group>

            <Form.Group controlId="name">
                <Form.Label>Name:</Form.Label>
                <Form.Control type="text" placeholder="Name" name="author" />
            </Form.Group>

            <Form.Group controlId="date">
                <Form.Label>Date:</Form.Label>
                <Form.Control type="text" placeholder="Date" name="date" />
            </Form.Group>

            <Form.Group controlId="task">
                <Form.Label>Task:</Form.Label>
                <Form.Control type="text" placeholder="Task" name="task" />
                <Button className="mt-3" variant="secondary" type="submit" onClick={handleAddTasks}>+</Button>
            </Form.Group>

            <Button className="mt-3" variant="warning" type="submit">
                Submit
            </Button>
        </Form>
    )

}

export default ListForm;
